func sanitizeInput(input string) string {
	var output string
	for _, char := range input {
		if char >= 'a' && char <= 'z' || char >= 'A' && char <= 'Z' || char >= '0' && char <= '9' || char == '_' || char == '-' || char == '.' || char == ',' {
			output += string(char)
		}
	}
	return output
}
func main() {
