window.drawQr = (url) => {
    const QRCode = require('qrcode').default; // Load the QR Code library
    const qrcode = new QRCode();

    return new Promise((resolve, reject) => {
        try {
            qrcode.makeCode(url, (err) => {
                if (err) reject(err);

                const img = document.createElement('img');
                img.src = qrcode.makeBytes();
                resolve(img);
            });
        } catch (error) {
            reject(error);
        }
    });
};
