function reverseSentenceOrder(string) {
  // Remove punctuation and special characters from the string
  const punctuationRegex = /[~`!@#$%^&*()_|+\-=?;:'",.<>\{\}\[\]\\\/]/g;
  const strippedString = string.replace(punctuationRegex, '');

  // Split the string into sentences based on punctuation marks
  const sentences = strippedString.split(/[.?!]/);

  // Reverse the order of words in each sentence
  const reversedSentences = sentences.map(sentence => {
    // Remove leading and trailing whitespaces from the sentence
    const trimmedSentence = sentence.trim();
    const words = trimmedSentence.split(/\s+/).reverse().join(' ');
    return words;
  });

  // Reconstruct the string with reversed sentences and original order of sentences
  const reversedString = '';
  const count = sentences.map(sentence => (sentence.trim().length)).reduce((sum, len) => sum + len, 0);
  for (let i = 0; i < reversedSentences.length; i++) {
    reversedString += sentences[i] + ' ' + reversedSentences[i] + '\n';
  }
  reversedString += '\n' + count + ' words.\n';

  return {
    originalString: string,
    reversedString: reversedString
  };
}
