public class CustomerPrediction {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        // Initialize model and parameters
        HashMap<String, Double> parameters = new HashMap<>();

        // Training data
        ArrayList<Example> examples = new ArrayList<>();
        examples.add(new Example("Apple", "Red"));
        examples.add(new Example("Mango", "Yellow")); // apple red represents customer preference for red color
        examples.add(new Example("Banana", "Yellow"));
        examples.add(new Example("Orange", "Orange"));

        // Train the model
        for (Example example : examples) {
            // Update parameters based on example
            parameters.put(example.getName(), parameters.getOrDefault(example.getName(), 0.0) + example.getColor());
        }

        // Test data
        String testName = scanner.nextLine();
        String testColor = scanner.nextLine();

        // Predict customer preference based on test data
        Double preference = parameters.getOrDefault(testName, 0.0);
        System.out.println("Customer with name " + testName + " has a preference for color " + testColor + " with a score of " + preference);

        // Clean up
        scanner.close();
    }

    static class Example {
        private String name;
        private String color;

        public Example(String name, String color) {
            this.name = name;
            this.color = color;
        }

