```python
from typing import Callable

class BigQueryRepository:
    def __init__(self, client: bigquery.Client):
        self.bq_client = client

    def run_query(self, cacheable_query: Callable[[str], list], cache_module='google.cloud.bigquery.job', query_params=None, **kwargs) -> list:
        cache_name = f'cached-query_{str(quantity(datetime.utcnow()).subseconds)}'
        cached_job = self.bq_client.get_job(query=cacheable_query.__name__, cache_name=cache_name)
        if cached_job:
            return cached_job.result()
        else:
            try:
                query_result = cacheable_query(query_params, **kwargs)
                self.bq_client.create_job(query_result=query_result, query_job_config=self.determine_job_config(query_result))
                return query_result
            except Exception as e:
                self.bq_client.create_job(query=queryable_query.__name__, cache_name=cache_name)
                return self.bq_client.get_job(query=queryable_query.__name__, cache_name=cache_name).result()
